import { Box, Button, Link, Typography, TextField, InputAdornment, InputLabel, Checkbox, FormControlLabel, FormGroup} from '@mui/material';
import ReactLogo2 from '../assets/background/2.svg';
import ReactLogo3 from '../assets/background/3.svg';
import ReactLogo1 from '../assets/background/1.svg';
import VisibilityIcon from '@mui/icons-material/Visibility';
import LockIcon from '@mui/icons-material/Lock';
import EmailIcon from '@mui/icons-material/Email';


export default function UseFormControl() {
  return (
      <Box>
        <Box component='img' src={ReactLogo2} alt="vv" sx={{
                position: 'absolute',
                bottom: '0'
            }}/>
         <Box component='img' src={ReactLogo3} alt="vv" sx={{
                position: 'absolute',
                right: '0'
            }}/>
         <Box component='img' src={ReactLogo1} alt="vv" sx={{
                position: 'absolute',
                right: 'calc(50% - 124px)',
                top: 'calc(50% - 21px)',
                display: 'none'
            }}/>
        <Box sx={{
            background: 'linear-gradient(131.46deg, #F8FAFB 28.17%, #EDF5FF 94.9%), #F8FAFB;',
            display: 'flex',
            justifyContent: 'center',
            height: '100vh',
            }}>
            <Box sx={{
                background: 'white',
                marginTop: 'auto',
                marginBottom: 'auto',
                width: '35%',
                height: '80%',
                borderRadius: '16px',
                textAlign: 'center',
                padding: '40px'
            }}>
                <Box width='50%'>
                <Typography variant='h3'>Sign In</Typography>
                <Typography variant='p'>Please sign up to your personal account if you want to use all our premium products</Typography>
                </Box>
                <Box>
                    <Box>
                    <InputLabel variant='label' sx={{textAlign: 'left', fontFamily: 'IBM Plex Sans', color: '#121F3E'}}>
                        Email
                    </InputLabel>
                    <TextField
                            type='email'
                            placeholder='Enter an email address'
                            InputProps={{
                            startAdornment: (
                                <InputAdornment position="start">
                                <EmailIcon/>
                                </InputAdornment>
                            )
                            }}
                            sx={{width: '100%'}}

                        />
                    </Box>
                    <Box>
                        <Box display="flex" justifyContent='space-between'>
                            <InputLabel variant='label' sx={{fontFamily: 'IBM Plex Sans', color: '#121F3E'}}>
                                Password
                            </InputLabel>
                            <Link underline='none' variant='a'>Forgot Password?</Link>
                        </Box>
                        <TextField
                            placeholder='Enter a password'
                            type='email'
                            InputProps={{
                            startAdornment: (
                                <InputAdornment position="start">
                                <LockIcon/>
                                </InputAdornment>
                            ),
                            endAdornment: (
                                <InputAdornment position="start">
                                <VisibilityIcon/>
                                </InputAdornment>
                            )
                            }}
                            variant="outlined"
                            sx={{
                                width: '100%',
                            }}
                        />
                    </Box>      
                </Box>
                <FormGroup>
                    <FormControlLabel control={<Checkbox />} label="Remember Me" sx={{

                    }}/>
                </FormGroup>
                <Button type="submit" sx={{ width: '100%' , background: '#1E86FF', color: 'white'}}>
                    Log In
                </Button>
                    OR
            </Box>
        </Box>
      </Box>

  );
}
