import * as React from 'react';
import { Box } from '@mui/system';
import InputLabel from '@mui/material/InputLabel';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { Button, Link, Typography } from '@mui/material';
import ReactLogo2 from '../assets/background/2.svg';
import ReactLogo3 from '../assets/background/3.svg';
import ReactLogo1 from '../assets/background/1.svg';

export default function UseFormControl() {
  return (
      <Box>
        <Box component='img' src={ReactLogo2} alt="vv" sx={{
                position: 'absolute',
                bottom: '0'
            }}/>
         <Box component='img' src={ReactLogo3} alt="vv" sx={{
                position: 'absolute',
                right: '0'
            }}/>
         <Box component='img' src={ReactLogo1} alt="vv" sx={{
                position: 'absolute',
                right: 'calc(50% - 124px)',
                top: 'calc(50% - 21px)'
            }}/>
        <Box sx={{
            background: 'linear-gradient(131.46deg, #F8FAFB 28.17%, #EDF5FF 94.9%), #F8FAFB;',
            display: 'flex',
            justifyContent: 'center',
            height: '100vh',
            }}>
            <Box sx={{
                background: 'white',
                marginTop: 'auto',
                marginBottom: 'auto',
                height: '50%',
                width: '30%'
            }}>
                <Typography>Sign In</Typography>
                <Typography>Please sign up to your personal account if you want to use all our premium products</Typography>
                <Formik
                    initialValues={{ email: '', password: '' }}
                    validate={values => {
                        const errors = {};
                        if (!values.email) {
                        errors.email = 'Required';
                        } else if (
                        !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                        ) {
                        errors.email = 'Invalid email address';
                        }
                        return errors;
                    }}
                    onSubmit={(values, { setSubmitting }) => {
                        setTimeout(() => {
                        alert(JSON.stringify(values, null, 2));
                        setSubmitting(false);
                        }, 400);
                    }}
                    >
                    {({ isSubmitting }) => (
                    <Form >
                        <InputLabel htmlFor="input-with-icon-adornment">
                            Email
                        </InputLabel>
                        <Field type="email" name="email" />
                        <ErrorMessage name="email" component="div" />
                        <Box display="flex" justifyContent='space-between'>
                            <InputLabel htmlFor="input-with-icon-adornment">
                                Password
                            </InputLabel>
                            <Link>Forgot Password?</Link>
                        </Box>
                        <Field type="text" name="password" />
                        <ErrorMessage name="password" component="div" />
                        <Button type="submit" disabled={isSubmitting} sx={{display: 'block' , width: '100%' , background: '#1E86FF', color: 'white'}}>
                            Log In
                        </Button>
                    </Form>
                    )}
                </Formik>
            </Box>
        </Box>
      </Box>

  );
}
