import * as React from 'react';
import { Box } from '@mui/system';
import { Button, Link, Typography, TextField, InputAdornment, InputLabel } from '@mui/material';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import ReactLogo2 from '../assets/background/2.svg';
import ReactLogo3 from '../assets/background/3.svg';
import ReactLogo1 from '../assets/background/1.svg';

export default function UseFormControl() {
  return (
      <Box>
        <Box component='img' src={ReactLogo2} alt="vv" sx={{
                position: 'absolute',
                bottom: '0'
            }}/>
         <Box component='img' src={ReactLogo3} alt="vv" sx={{
                position: 'absolute',
                right: '0'
            }}/>
         <Box component='img' src={ReactLogo1} alt="vv" sx={{
                position: 'absolute',
                right: 'calc(50% - 124px)',
                top: 'calc(50% - 21px)'
            }}/>
        <Box sx={{
            background: 'linear-gradient(131.46deg, #F8FAFB 28.17%, #EDF5FF 94.9%), #F8FAFB;',
            display: 'flex',
            justifyContent: 'center',
            height: '100vh',
            }}>
            <Box sx={{
                background: 'white',
                marginTop: 'auto',
                marginBottom: 'auto',
                height: '50%',
                width: '30%',
                textAlign: 'center'
            }}>
                <Typography>Sign In</Typography>
                <Typography>Please sign up to your personal account if you want to use all our premium products</Typography>
                <Box>
                    <Box>
                    <InputLabel htmlFor="input-with-icon-adornment">
                        Email
                    </InputLabel>
                    <TextField
                        value="TextField"
                        InputProps={{
                        startAdornment: (
                            <InputAdornment position="start">
                            <AccountCircle />
                            </InputAdornment>
                        ),
                        endAdornment: (
                            <InputAdornment position="start">
                            <AccountCircle />
                            </InputAdornment>
                        )
                        }}
                        variant="outlined"
                    />
                    </Box>
                    <Box>
                        <Box display="flex" justifyContent='space-around'>
                            <InputLabel htmlFor="input-with-icon-adornment">
                                Password
                            </InputLabel>
                            <Link>Forgot Password?</Link>
                        </Box>
                        <TextField
                            value="TextField"
                            InputProps={{
                            startAdornment: (
                                <InputAdornment position="start">
                                <AccountCircle />
                                </InputAdornment>
                            ),
                            endAdornment: (
                                <InputAdornment position="start">
                                <AccountCircle />
                                </InputAdornment>
                            )
                            }}
                            variant="outlined"
                        />
                    </Box>
                        <FormGroup>
                        <FormControlLabel control={<Checkbox />} label="Remember Me" />
                        </FormGroup>
                        <Button type="submit" sx={{display: 'block' , width: '100%' , background: '#1E86FF', color: 'white'}}>
                            Log In
                        </Button>
                        OR
                </Box>
            </Box>
        </Box>
      </Box>

  );
}
